package ua.com.homebudget.model;import com.fasterxml.jackson.annotation.JsonIgnore;import lombok.Data;import lombok.EqualsAndHashCode;import javax.persistence.*;import java.io.Serializable;import java.util.Set;/** * The entity class for the "accounts" database table. * * @author Stefansky Alex * @author Bondar Dmytro */@Data@Entity@Table(name = "accounts")@EqualsAndHashCode(exclude = {"incomes", "expenses"})public class Account implements Serializable {    private static final long serialVersionUID = 3206585920990206281L;    @Id    @SequenceGenerator(name = "ACCOUNT_ID_GENERATOR", sequenceName = "ID")    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "ACCOUNT_ID_GENERATOR")    @Column(name = "account_id")    private Integer accountId;    @ManyToOne    @JoinColumn(name = "user_id")    private User user;    @Column(name = "name")    private String name;    @JsonIgnore    @OneToMany(mappedBy = "account", fetch = FetchType.LAZY)    private Set<Expense> expenses;    @JsonIgnore    @OneToMany(mappedBy = "account", fetch = FetchType.LAZY)    private Set<Income> incomes;}