package ua.com.homebudget.repository;import com.github.springtestdbunit.annotation.DatabaseSetup;import org.junit.Assert;import org.junit.Test;import org.springframework.beans.factory.annotation.Autowired;import ua.com.homebudget.DblIntegrationTest;import ua.com.homebudget.model.User;@DatabaseSetup("user.xml")public class UserRepositoryTest extends DblIntegrationTest {    @Autowired    UserRepository userRepository;    @Test    public void testFindById() throws Exception {        User user = userRepository.findOne(1);        Assert.assertNotNull(user);    }    @Test    public void testFindByUnexistingId() throws Exception {        User user = userRepository.findOne(1131);        Assert.assertNull(user);    }    @Test    public void testFindByEmail() throws Exception {        User user = userRepository.findByEmail("some@mail.com");        Assert.assertNotNull(user);    }    @Test    public void testFindByUnexistingEmail() throws Exception {        User user = userRepository.findByEmail("some11111@mail.com");        Assert.assertNull(user);    }}